mapper.select:
SELECT EXTRACT(DAY FROM created_at)::int AS created_at
FROM event_attendance_detail
WHERE user_id = #{userId}

    AND TO_CHAR(created_at, 'MM') = #{month}


    AND TO_CHAR(created_at, 'YYYY-MM-DD') = TO_CHAR(#{createdAt}, 'YYYY-MM-DD')

SELECT event_friends_roulette_current_count FROM event_friends_detail
WHERE event_info_id = #{eventInfoId}
  AND user_id = #{userId}

mapper.update:
UPDATE event_friends_detail
SET roulette_count = roulette_count + 1,
updated_at = CURRENT_TIMESTAMP
WHERE event_info_id = #{eventInfoId}
AND user_id = #{userId}
AND roulette_count < 3

UPDATE event_friends_detail
SET roulette_count = roulette_count - 1,
    updated_at = CURRENT_TIMESTAMP
WHERE event_info_id = #{eventInfoId}
  AND user_id = #{userId}
  AND roulette_count > 0

mapper.select:
SELECT * FROM coupon_template
WHERE event_type_cd = #{eventTypeCd}
ORDER BY RANDOM() LIMIT 1

SELECT COUNT(*) > 0
FROM coupon_info
WHERE user_id = #{userId}
  AND template_id = #{templateId}
